// Espera: 1 item con json.body (pedido) y binary con keys photos_0, photos_1...
const item = items[0];
const { json, binary } = item;

const body = json.body || {};
const customerName = (body.name || body.customerName || "cliente_sin_nombre").toString().trim();

// orderId seguro (sin ":" / espacios)
const rawId = body.order_id || json.orderId || $now; // $now = ISO string
const orderId = rawId.toString().replace(/[^0-9A-Za-z_-]/g, "_");

const folderName = `${customerName.replace(/[^\w\- ]/g, "_")}__${orderId}`;

const out = [];
if (binary) {
  // aceptar photos, file, image...
  const keys = Object.keys(binary).filter(k =>
    k.startsWith('photos') || k.startsWith('file') || k.startsWith('image')
  );

  if (keys.length === 0) {
    return [{
      json: { ...json, folderName, orderId, hasPhotos: false }
    }];
  }

  for (const k of keys) {
    const b = binary[k]; // {data, fileName, mimeType, ...}
    out.push({
      json: {
        ...json,                 // conserva json original (con headers/body si te interesa)
        folderName,
        orderId,
        hasPhotos: true,
        photoFilename: b.fileName || `${orderId}_${k}`,
        photoMime: b.mimeType || 'application/octet-stream',
      },
      binary: {
        photo: b                 // renombrado a 'photo' (clave que usaremos en Upload)
      }
    });
  }
} else {
  return [{
    json: { ...json, folderName, orderId, hasPhotos: false }
  }];
}

return out;

